import { useNavigate } from 'react-router-dom'

// components
import Button from './Button'
import Accordion from 'react-bootstrap/Accordion'
import c from '@mui/icons-material/DeleteForever';

// types
import { IData } from '../interface/commonInterface'

// API
import { deleteItems } from '../api/shareAPI'
import { useContext } from 'react'
import { ShareDispatchContext } from '../App'

const ShareItem = ({
  shareId,
  broadcastNo,
  srcImg,
  linkText,
  title,
  tipText,
}: IData) => {
  const navigate = useNavigate()
  const { fetchData } = useContext(ShareDispatchContext)

  const goEdit = () => {
    navigate(`/edit/${shareId}`)
  }
  const goDetail = () => {
    navigate(`/detail/${shareId}`)
  }
  const onDelete = async () => {
    const params = {
      broadcastNo: broadcastNo,
      shareId: shareId,
    }

    try {
      const response = await deleteItems(params)
      if (response.status === 200) {
        fetchData(1)
      }
    } catch (error) {
      console.error('Error:', error)
    }
  }
  return (
    <div className="ShareItem">
      <div className="contents">
        <Accordion.Item eventKey={`${shareId}`}>
          <Accordion.Header>
            <div className="img_wrapper">
              <img src="/assets/afreecatv_logo.jpg" alt="defaultImg" />
            </div>
            <div className="shareTitle">{title}</div>
          </Accordion.Header>
          <Accordion.Body>
            <div>{tipText}</div>
            <div className="btn_wrapper">
              <div>
                <Button text="수정하기" onClick={goEdit} />
              </div>
              <div>
                <Button text="삭제하기" type="cancel" onClick={onDelete} />
              </div>
            </div>
          </Accordion.Body>
        </Accordion.Item>
      </div>
    </div>
  )
}

export default ShareItem
