import { useContext, useEffect, useRef, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { ShareDataContext } from '../App'
import { addItems, editItems } from '../api/shareAPI'

// components
import Button from './Button'

// types
import { IData } from '../interface/commonInterface'

interface EditorProps {
  isEdit?: boolean
  originData?: IData
}

const Editor: React.FC<EditorProps> = ({ isEdit, originData }) => {
  const dataList = useContext(ShareDataContext)
  const broadcastNo = dataList[0]?.broadcastNo
  const navigate = useNavigate()
  const titleRef = useRef<HTMLInputElement>(null)
  const [shareTitle, setShareTitle] = useState<string>('')
  const [shareLink, setShareLink] = useState<string>('')
  const [shareDes, setShareDes] = useState<string>('')

  useEffect(() => {
    if (isEdit) {
      if (originData) {
        setShareTitle(originData.title)
        setShareLink(originData.linkText)
        setShareDes(originData.tipText)
      }
    }
  }, [isEdit, originData])

  const handleSubmit = async () => {

    if (shareTitle.length < 1) {
      titleRef.current?.focus()
      return
    }

    const params = {
      broadcastNo: broadcastNo,
      srcImg: '',
      linkText: shareLink,
      title: shareTitle,
      tipText: shareDes,
    }

    try {
      if(isEdit){

      }
      const response = await addItems(params)
      if (response.status === 200) {
        navigate('/')
      }
    } catch (error) {
      console.error('Error fetching data:', error)
    }
  }

  return (
    <div className="Editor">
      <section>
        <div className="img_wrapper">
          <img src="/assets/afreecatv_logo.jpg" alt="defaultImg" />
        </div>
      </section>
      <section>
        <div className="sectionTitle">
          <p>제목</p>
        </div>
        <div className="input_box">
          <input
            ref={titleRef}
            value={shareTitle}
            className="input_title"
            type="text"
            placeholder="공유하고 싶은 정보를 입력해주세요."
            onChange={(e) => setShareTitle(e.target.value)}
          ></input>
        </div>
      </section>
      <section>
        <div className="sectionTitle">
          <p>링크</p>
        </div>
        <div className="input_box">
          <input
            value={shareLink}
            className="input_link"
            type="text"
            placeholder="링크가 있다면 입력해주세요."
            onChange={(e) => setShareLink(e.target.value)}
          ></input>
        </div>
      </section>
      <section>
        <div className="sectionTitle">
          <p>내용</p>
        </div>
        <div className="input_box">
          <textarea
            value={shareDes}
            placeholder="추가적인 내용이 필요하다면 입력해주세요."
            onChange={(e) => setShareDes(e.target.value)}
          ></textarea>
        </div>
      </section>
      <section>
        <div className="editorpage_btn">
          <Button
            text="취소"
            type="cancel"
            onClick={() => navigate(-1)}
          ></Button>
          <Button
            text={isEdit ? '수정' : '등록'}
            onClick={handleSubmit}
          ></Button>
        </div>
      </section>
    </div>
  )
}
export default Editor
