import { useNavigate } from 'react-router-dom'

// components
import Button from './Button'
import Accordion from 'react-bootstrap/Accordion'

// types
import { IData } from '../interface/commonInterface'

// API
import { deleteItems } from '../api/shareAPI'
import { useContext } from 'react'
import { ShareDispatchContext } from '../App'

const ShareItem = ({
  shareId,
  broadcastNo,
  srcImg,
  linkText,
  title,
  tipText,
}: IData) => {
  const navigate = useNavigate()
  const { fetchData } = useContext(ShareDispatchContext)

  const goEdit = () => {
    navigate(`/edit/${shareId}`)
  }
  const goDetail = () => {
    navigate(`/detail/${shareId}`)
  }
  const onDelete = async () => {
    const params = {
      broadcastNo: broadcastNo,
      shareId: shareId,
    }

    try {
      const response = await deleteItems(params)
      if (response.status === 200) {
        fetchData(1)
      }
    } catch (error) {
      console.error('Error:', error)
    }
  }
  return (
      <Accordion.Item eventKey={`${shareId}`}>
        <Accordion.Header>
          <div className="img_wrapper">
            <img src="/assets/afreecatv_logo.jpg" alt="defaultImg" />
          </div>
          Accordion Item #1
        </Accordion.Header>
        <Accordion.Body>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat. Duis aute irure dolor in
          reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
          pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
          culpa qui officia deserunt mollit anim id est laborum.
        </Accordion.Body>
      </Accordion.Item>
  )
}

export default ShareItem
