import './App.css'
import { useEffect } from 'react'
import { Routes, Route, useLocation } from 'react-router-dom'
// components
import Home from './pages/Home'
import New from './pages/New'
// types
import {
  IAuthInfo,
  IBroadInfo,
  ICreateApiRequest,
} from './interface/commonInterface'
import getItems from './api/shareAPI'

function App() {
  const { pathname } = useLocation()
  console.log(process.env.REACT_APP_SERVER)

  const fetchData = async (broadNo: number) => {
    try {
      const response = await getItems({broadNo})

      // 여기서 response 사용
      console.log(response.data)
    } catch (error) {
      console.error('Error fetching data:', error)
    }
  }

  useEffect(() => {
    const SDK = window.AFREECA.ext
    const extensionSDK = SDK()
    extensionSDK.handleInitialization(
      (authInfo: IAuthInfo, broadInfo: IBroadInfo) => {
        if (authInfo.isBJ) {
          console.log(broadInfo.broadNo);

          fetchData(broadInfo.broadNo)
        }
      }
    )
  }, [])

  return (
    <div className="App">
      {pathname === '/index.html' ? <Home /> : ''}
      <Routes>
        <Route path="/" element={<Home />}></Route>
        <Route path="/new" element={<New />}></Route>
      </Routes>
    </div>
  )
}

export default App
