import { useEffect, useRef, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useContext } from 'react'
import { ShareDataContext } from '../App'

// components
import Button from '../components/Button'

// API
import { addItems } from '../api/shareAPI'

const New = () => {
  const dataList = useContext(ShareDataContext)
  const broadcastNo = dataList[0].broadcastNo
  const navigate = useNavigate()
  const titleRef = useRef<HTMLInputElement>(null)
  const [shareTitle, setShareTitle] = useState<string>('')
  const [shareLink, setShareLink] = useState<string>('')
  const [shareDes, setShareDes] = useState<string>('')

  const handleSubmit = async () => {
    if (shareTitle.length < 1) {
      titleRef.current?.focus()
      return
    }

    const params = {
      broadcastNo: broadcastNo,
      srcImg: '',
      linkText: shareLink,
      title: shareTitle,
      tipText: shareDes
    }

    try {
      const response = await addItems(params)
    } catch (error) {
      console.error('Error fetching data:', error)
    }
  }

  return (
    <div className="New">
      <section>
        <div className="img_wrapper">
          <img src="/assets/afreecatv_logo.jpg" alt="defaultImg" />
        </div>
      </section>
      <section>
        <div className="sectionTitle">
          <p>제목</p>
        </div>
        <div className="input_box">
          <input
            ref={titleRef}
            value={shareTitle}
            className="input_title"
            type="text"
            placeholder="공유하고 싶은 정보를 입력해주세요."
            onChange={(e) => setShareTitle(e.target.value)}
          ></input>
        </div>
      </section>
      <section>
        <div className="sectionTitle">
          <p>링크</p>
        </div>
        <div className="input_box">
          <input
            className="input_link"
            type="text"
            placeholder="링크가 있다면 입력해주세요."
            onChange={(e) => setShareLink(e.target.value)}
          ></input>
        </div>
      </section>
      <section>
        <div className="sectionTitle">
          <p>내용</p>
        </div>
        <div className="input_box">
          <textarea
            placeholder="추가적인 내용이 필요하다면 입력해주세요."
            onChange={(e) => setShareDes(e.target.value)}
          ></textarea>
        </div>
      </section>
      <section>
        <div className="newpage_btn">
          <Button
            text="취소"
            type="cancel"
            onClick={() => navigate(-1)}
          ></Button>
          <Button text="등록" onClick={handleSubmit}></Button>
        </div>
      </section>
    </div>
  )
}

export default New
