import './App.css'
import { useEffect } from 'react'
import { Routes, Route, useLocation } from 'react-router-dom'
// components
import Home from './pages/Home'
import New from './pages/New'
// types
import {
  IAuthInfo,
  IBroadInfo,
  ICreateApiRequest,
} from './interface/commonInterface'
import axios from 'axios'

function App() {
  const { pathname } = useLocation()

  const fetchData = async () => {
    try {
      const response = await axios.post('http://localhost:8081/v1/api/create', {
        params: {
          shareId: 0,
          broadcastNo: 0,
          srcImg: '1',
          linkText: '1',
          title: '1',
          tipText: '1',
        } as ICreateApiRequest,
      })

      // 여기서 response 사용
      console.log(response.data)
    } catch (error) {
      console.error('Error fetching data:', error)
    }
  }

  useEffect(() => {
    const SDK = window.AFREECA.ext
    const extensionSDK = SDK()
    // extensionSDK.handleInitialization(
    //   (authInfo: IAuthInfo, broadInfo: IBroadInfo) => {
    //     if (authInfo.isBJ) {
    //       console.log(broadInfo)
    //     }
    //   }
    // )
  }, [])

  return (
    <div className="App">
      {pathname === '/index.html' ? <Home /> : ''}
      <Routes>
        <Route path="/" element={<Home />}></Route>
        <Route path="/new" element={<New />}></Route>
      </Routes>
    </div>
  )
}

export default App
