import './App.css'
import { createContext, useEffect, useReducer } from 'react'
import { Routes, Route, useLocation } from 'react-router-dom'
// components
import Home from './pages/Home'
import New from './pages/New'
// types
import {
  IAuthInfo,
  IBroadInfo,
  IData,
  IAction,
} from './interface/commonInterface'
import getItems from './api/shareAPI'

//reducer
const reducer = (state: IData[], action: IAction) => {
  switch (action.type) {
    case 'FETCH_SUCCESS':
      return { ...state, ...action.payload }
    default:
      return state
  }
}

// initialShareData
const initialData: IData[] = []

//context
export const ShareDataContext = createContext(undefined)

function App() {
  const [data, dispatch] = useReducer(reducer, initialData)
  const { pathname } = useLocation()

  const fetchData = async (broadNo: number) => {
    try {
      const response = await getItems({ broadcastNo: broadNo })
      dispatch({ type: 'FETCH_SUCCESS', payload: response.data })
    } catch (error) {
      console.error('Error fetching data:', error)
    }
  }

  useEffect(() => {
    const SDK = window.AFREECA.ext
    const extensionSDK = SDK()
    extensionSDK.handleInitialization(
      (authInfo: IAuthInfo, broadInfo: IBroadInfo) => {
        if (authInfo.isBJ) {
          fetchData(broadInfo.broadNo)
        }
      }
    )
  }, [])

  return (
    <ShareDataContext.Provider value>
      <div className="App">
        {pathname === '/index.html' ? <Home /> : ''}
        <Routes>
          <Route path="/" element={<Home />}></Route>
          <Route path="/new" element={<New />}></Route>
        </Routes>
      </div>
    </ShareDataContext.Provider>
  )
}

export default App
